import React,{useState,useContext,useEffect} from "react";
import {ProductsContext} from './Context';
import {Link,useHistory} from 'react-router-dom';
import styled from 'styled-components';

const Details = (props) => {
  const history=useHistory();
  const value=useContext(ProductsContext);
const [product, setproduct] = useState({})
  //const id=props.match.params.id;
  useEffect(() => {
    const item=value.productsList.find(x=>x.id==props.match.params.id);
    
   setproduct({...item});
    console.log(product);
  }, [])
   const {
    title,
    price,
    color,
    image,
    type,
    brand,
    description
  }=product;
  console.log(product);
  return (
  <div className='container'>
  <div className='row'>
  <div className='col-10 mx-auto text-center py-4'>
 <H2>{title}</H2>
  </div>
  <div className='col-10 mx-auto col-md-6 my-3'>
  <img className='img-fluid' src={image} alt='product'/>
  </div>
  <div className='col-10 mx-auto col-md-6 my-3'>
  <h2>Brand: {brand} - {}</h2>
  <h4>Type : {type}</h4>
  <h4>Price: {price} Rs</h4>
  <h4>Description:</h4>
  <P className='font-weight-bold mt-3'>{description}</P>
  <div className='justify-content-center'>
  <Button onClick={()=>history.goBack()}>Back</Button>
  <Button onClick={()=>console.log('click!')}>Add to Cart</Button>
  </div>
  </div>
</div>999999
  </div>
  );
};
const H2=styled.h2`
color:grey;
font-family:'Yanone Kaffeesatz', sans-serif;
`;
const P=styled.p`
font-family:'Arimo', sans-serif;
`
const Button=styled.button`
text-transform:capitalize;
background-color:palevioletred;
border:2px solid palevioletred;
border-radius:4px;
 padding: 0.5rem;
  color: whiteSmoke;
  margin-bottom: 0.5rem;
  margin-right:1.6rem;
  transition:all 0.3s ease-in-out;
&:hover {
  background:#d4af37;
  color:;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
  };
  border:#e3f9a6

`
export default Details;
